package coreJava;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x =10;
		int y= 20;
		
		if(x>y) {
			System.out.println(x);
			System.out.println("X is greater");
		}
		else{
			System.out.println(y);
			System.out.println("Y is greater");
		}
	}

}
